<?php
 class import_register_agency_success extends UpdateModel { public $title = 'Импорт уведомления об успешной регистрации'; public function insert($file, $user_code) { $sql = $this->db->formatExec( $this->OFFICEDB . '.dbo.up_WEB_3_ADMIN_tour_config', [ 'Section' => 'register_agency', 'What' => 'SUCCESS', 'Value' => file_get_contents($file), 'UserCode' => $user_code, ] ); $this->db->query($sql); } public function make() { if (!defined('INTERNET_USER')) { $sql = $this->db->formatExec( $this->OFFICEDB . '.dbo.up_WEB_3_default_usercode' ); $INTERNET_USER = $this->db->fetchOne($sql); } else { $INTERNET_USER = INTERNET_USER; } $tpl = _ROOT . '/data/register_agency/success.tpl'; if (!file_exists($tpl)) { $tpl = _ROOT . '/templates/register_agency/3_success.tpl'; } if (file_exists($tpl)) { $this->insert($tpl, $INTERNET_USER); } return 0; } } 