<?php
class DocCategory { const UNKNOWN = -1; const INVOICE = 1; const INSURANCE_MEDICAL = 3; const VOUCHER = 4; const CONTRACT = 5; const TICKET_COST = 7; const TICKET = 8; const BOOKLET = 10; const CONFIRMATION = 11; const VISA = 12; const AGREEMENT_PERSON = 17; const EMAIL = 19; const RECEIPT = 20; const INSURANCE_CANCEL = 21; const AGREEMENT_AGENCY = 23; const SERVICE_VOUCHER = 24; const MONEY_BACK = 25; static public function to_module($value) { $doccategory = [ self::INVOICE => 'invoice', self::INSURANCE_MEDICAL => 'insurance', self::VOUCHER => 'voucher', self::CONTRACT => 'agreement', 6 => 'claim_act', self::TICKET_COST => 'aviaticket_cost', self::TICKET => 'aviaticket', 9 => 'anketa', self::BOOKLET => 'booklet', self::CONFIRMATION => 'confirmation', self::VISA => 'visa', 16 => 'warrant', self::AGREEMENT_PERSON => 'agreement_person', 18 => 'cancel_claim', self::RECEIPT => 'psbank', self::INSURANCE_CANCEL => 'insurance', ]; if (!is_int($value)) { unset($doccategory[self::INSURANCE_CANCEL]); $doccategory = array_flip($doccategory); } return array_key_exists($value, $doccategory) ? $doccategory[$value] : null; } static public function by_module($module) { return self::to_module($module); } } 