<?php
 class Claim_Unpaid_Controller extends Samo_Controller { public function default_action() { $this->view->assign('PERSONAL_AREA', Profile_Model::links()); $showResult = (Samo_Request::bitval('EARLYCOMISSION') !== null); if ($showResult) { $this->view->assign('claims', $this->model->claim_list()) ->assign('DEPOSIT_INC', $this->model->defaults['DEPOSIT']) ->assign('DEPOSIT_SUM', $this->model->defaults['DEPOSIT_SUM']) ->assign('DEPOSIT_CURRENCY_ALIAS', $this->model->defaults['DEPOSIT_CURRENCY_ALIAS']); } $this->view->assign('SHOW_RESULT', $showResult) ->assign('EARLYCOMISSION', Samo_Request::bitval('EARLYCOMISSION')) ->assign('NEARFUTURE', Samo_Request::bitval('NEARFUTURE')); parent::default_action(); } } 