<?php
 class Popular_Hotel_Controller extends Search_Tour_Controller { public function construct() { $this->actions = array('TOWNFROMINC' => array('STATEINC'),); $this->options_only = array('STATEINC', 'TOWNFROMINC'); $this->persistent = array('STATEINC', 'TOWNFROMINC'); } public function default_action() { $this->default_app_env(); $this->view->assign('PERIOD', $this->model->defaults['PERIOD']) ->assign('TOWNFROMINC', $this->model->getTOWNFROMINC()) ->assign('STATEINC', $this->model->getSTATEINC()) ->assign('RESULT', $this->model->getResult()) ->render('layout'); } public function samo_action($action) { parent::samo_action($action); $this->view->assign('RESULT', $this->model->getResult()) ->element_update('resultset', 'resultset.tpl'); } } 