<?php
 class Schedule_doc_Controller extends Samo_Controller { protected $embeddable = 'default_action'; public function construct() { $this->persistent = array('TOWNFROMINC'); } public function default_action() { $this->default_app_env(); $this->view ->assign('TOWNFROM', $this->model->getTOWNFROMINC()) ->assign('STATE', $this->model->getSTATEINC()) ->assign('OWNER', $this->model->getOWNERINC()) ->assign('RESULT', $this->model->schedule_doc()) ->render('layout'); } public function TOWNFROMINC() { $this->view ->add_options('STATEINC', $this->model->getSTATEINC()) ->add_options('OWNERINC', $this->model->getOWNERINC()) ->assign('RESULT', $this->model->schedule_doc()) ->element_update('resultset', 'resultset.tpl'); } public function STATEINC() { $this->view ->add_options('OWNERINC', $this->model->getOWNERINC()) ->assign('RESULT', $this->model->schedule_doc()) ->element_update('resultset', 'resultset.tpl'); } public function SHEDULE() { $this->view->assign('RESULT', $this->model->schedule_doc()) ->element_update('resultset', 'resultset.tpl'); } } 