 class The_Best_Model extends Search_Tour_Model { protected $api_name = 'The_Best_Api'; public function construct() { $this->defaults['TOWNTOINC'] = Samo_Request::intval('TOWNTOINC'); $this->defaults['GROUPSTARINC'] = Samo_Request::intval('GROUPSTARINC'); $this->defaults['VIPTYPEINC'] = Samo_Request::intval('VIPTYPEINC'); $this->defaults['FILTER'] = Samo_Request::intval('FILTER'); } public function getSTATEINC() { $res = $this->api()->getSTATES(); return array_merge( array(array('id' => 0, 'name' => '----', 'nameAlt' => '----', 'selected' => false)), ($res ? $res : array()) ); } public function getTOWNTOINC() { $res = $this->api()->getTOWNS(); return array_merge( array(array('id' => 0, 'name' => '----', 'nameAlt' => '----', 'selected' => false)), ($res ? $res : array()) ); } public function getGROUPSTARINC() { $res = $this->api()->getSTARS(); return array_merge( array(array('id' => 0, 'name' => '----', 'nameAlt' => '----', 'selected' => false)), ($res ? $res : array()) ); } public function getResult() { return $this->api()->getPRICES(); } } 